import React, { Component } from 'react';
import ReactDOM from "react-dom";
import Marker from './Marker';

const shelterAPI = "http://localhost:5000/api/shelters";

class ShelterAPI extends Component {
    constructor(props) {
        super(props);
        this.state = {
            shelters: [],
            isLoaded: false,
        };
      }
      componentDidMount() {
        fetch(shelterAPI)
          .then(res => res.json())
          .then(result => {
            this.setState({
              isLoaded: true,
              shelters: result
            });
          });
      }
    render() {
        const { shelters } = this.state;
        if (!isLoaded) {
            return <div>Loading ... </div>;
          } else {
         return (
             <div className="App">
                 <h1></h1>
                 {shelters.map(shelter =>
                 <h2>shelter.address</h2>
               )}
             </div>
         );
                 }
     }
 }
 export default ShelterAPI;
 ReactDOM.render(<ShelterAPI />, document.getElementById("app"));
