import React, { Component } from 'react';
import ReactDOM from "react-dom";

import PropTypes from 'prop-types';
import styled from 'styled-components';

const Wrapper = styled.div`
    position: absolute;
    width: 38px;
    height: 37px;
    background-image: url(https://icon-library.com/images/pin-icon-png/pin-icon-png-9.jpg);
    background-size: contain;
    background-repeat: no-repeat;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-transform: translate(-50%,-50%);
    -ms-transform: translate(-50%,-50%);
    transform: translate(-50%,-50%);
    cursor: grab;
`;

const Marker = ({ text, onClick }) => (
    <Wrapper
        alt={text}
        onClick={onClick}
    />
);

Marker.defaultProps = {
    onClick: null,
};

Marker.propTypes = {
    onClick: PropTypes.func,
    text: PropTypes.string.isRequired,
};

const shelterAPI = "http://localhost:5000/api/shelters";

class ShelterAPI extends Component {
    constructor(props) {
        super(props);
        this.state = {
            shelters: [],
        };
      }
      componentDidMount() {
        fetch(shelterAPI)
          .then(res => res.json())
          .then(result => {
            this.setState({
              shelters: result
            });
          });
      }
    render() {
        const { shelters } = this.state;
         return (
             <div>
                   
                 {shelters.map(shelter =>
                 
                 <Marker
                 text={shelter.address}
                 lat={shelter.latitude}
                 lng={shelter.longitude}
                />
                
               )}
               </div>
         );
                 
     }
 }
 export default ShelterAPI;
 ReactDOM.render(<ShelterAPI />, document.getElementById("root"));
