import React, { useState, useEffect } from "react";
import axios from "axios";
import Marker from './Marker';

const shelterAPI = "http://localhost:5000/api/shelters";

class ShelterAPI extends Component {
    constructor(){
        super();
        this.state ={users: []};
    }
    componentDidMount() {
           fetch(shelterAPI)
             .then(res => {
                 console.log(res);
                 return res.json()
              })
             .then(users => { 
                 console.log(users); 
                 this.setState({ users })
              });
          }
    render() {
         return (
             <div className="App">
                 <h1>Users</h1>
                 {this.state.users.map(user =>
                 <div key={user.id}>user: {user.name} Password: {user.password}</div>
               )}
             </div>
         );
     }
 }
 export default ShelterAPI;
