import React, {Component} from 'react';
import Marker from './Marker';

export default class ShelterAPI extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            entries: []
        }
    }
    componentDidMount() {
      this.fetchApiToEntries('http://localhost:5000/api/shelters');
    }
    fetchApiToEntries = (apiToFetch) => {
        fetch(apiToFetch)
            .then(result => result.json())
            .then((entries) => {
                this.setState({
                    ...this.state,
                    entries
                })
            })
            .catch((error) => console.log(error));
    }
